/**
 * @fileoverview Types for the config-array package.
 * @author Nicholas C. Zakas
 */
export interface ConfigObject {
    /**
     * The files to include.
     */
    files?: string[];
    /**
     * The files to exclude.
     */
    ignores?: string[];
    /**
     * The name of the config object.
     */
    name?: string;
    [key: string]: unknown;
}

